        //Simple
        public string DevuelveValorBuscado(string xpath, string url)
        {
            string valorBuscado = null;
            //objeto web mediante el cual nos comunicamos con la web 
            HtmlWeb web = new HtmlWeb();

            //Set tipo codifcacion para acentos.
            web.OverrideEncoding = Encoding.UTF8;

            //objeto que recoge el html
            HtmlDocument doc = null;

            //Pasamos la url al objeto web y el resultado, lo cargamos en el objeto doc
            doc = web.Load(url);

            //Creamos el objeto documentNode Que se encarga de diferenciar cada una de las html tags
            
            HtmlNode documentNode = doc.DocumentNode;

            //Creamos un objeto Lista de Nodes
            HtmlNodeCollection nodes = documentNode.SelectNodes(xpath);

            if (nodes != null)
            {
                foreach (HtmlNode node in nodes)
                {
                    //El método InnerAtml del objeto node nos devuelve el texto del interior de la tag
                    string precioString = node.InnerHtml;
                    if (precioString != null)
                    {
                        valorBuscado = precioString;
                        Console.WriteLine(DateTime.Now.ToString() + " " + precioString);
                        Console.WriteLine("");
                        Console.WriteLine("");
                    }
                    //break;
                }
            }
            
            return valorBuscado;
        }

        //Complejo
        public string DevuelveValorBuscado(string xpath, string url, string html, bool conProxy, string attribute)
        {
            string valorBuscado = null;

            //objeto que recoge el html
            HtmlDocument doc = null;

            doc = DevuelveDoc(url, html, conProxy);

            if (doc!=null)
            {
                valorBuscado = DetectaValorConXpath(doc, xpath, attribute);
            }

            return valorBuscado;
        }

        #region DevuelveDoc
        public HtmlDocument DevuelveDoc(string url, string html, bool conProxy)
        {
            //objeto que recoge el html
            HtmlDocument doc = null;

            //Desde la Url
            if (url != null)
            {
                doc = DevuelveDoc(url, conProxy);
            }
            else
            {
                doc = DevuelveDoc(html);
            }

            return doc;
        }

        //SIN URL
        public HtmlDocument DevuelveDoc(string html)
        {
            HtmlDocument doc = null;
            doc = new HtmlDocument();
            doc.LoadHtml(html);

            return doc;
        }

        //CON URL
        public HtmlDocument DevuelveDoc(string url, bool conProxy)
        {
            //objeto web mediante el cual nos comunicamos con la web 
            HtmlWeb web = new HtmlWeb();
            //Set tipo codifcacion para acentos.
            web.OverrideEncoding = Encoding.UTF8;
            //Darle tiempo al navegador
            web.BrowserTimeout = new TimeSpan(0, 0, 1000);

            //objeto que recoge el html
            HtmlDocument doc = null;

            //SIN PROXY
            if (conProxy == false)
            {
                //Pasamos la url al objeto web y el resultado, lo cargamos en el objeto doc
                doc = web.Load(url);

                bool noHaDevueltoError = NoHaDevueltoError(doc.Text);
                //Testeamos
                if (noHaDevueltoError == false)
                {
                    string eliminar = "";
                    //return null;
                }
            }
            else
            {//CON PROXI
                string proxyUrl = "";
                ScProxyService proxyService = new ScProxyService();
                WebProxy proxy = null;

                int cuentaErrores = 0;
                bool noHaDevueltoError = false;
                while (noHaDevueltoError == false)
                {
                    proxyUrl = proxyService.GetProxy(cuentaErrores);
                    proxy = new WebProxy(proxyUrl);

                    //// TRET D'AQUESTA PÀGINA: https://bLogHelper.jongallant.com/2012/07/htmlagilitypack-windows-authentication/
                    //web.PreRequest = delegate (HttpWebRequest webRequest)
                    //{
                    //    webRequest.Timeout = 30000;
                    //    return true;
                    //};
                    doc = web.Load(url, "GET", proxy, CredentialCache.DefaultNetworkCredentials);

                    //Testeamos doc
                    noHaDevueltoError = NoHaDevueltoError(doc.Text);
                    cuentaErrores = cuentaErrores +1; 
                }
            }

            return doc;
        }
