        #region CompruebaElBaneo
        public bool NoHaDevueltoError(string htmlData)
        {
            //SOLAMENTE RESPUESTAS QUE IMPLIQUEN QUE HA SIDO BANEADO
            //NO PONER RESPUESTAS DE ERROR POR NO ENCONTRAR EL VALORBUSCADO
            if (htmlData == "")
            {
                return false; //NoEsta
            }
            //CSRFToken
            if (htmlData.ToLower().Contains("CSRFToken".ToLower()))
            {
                return true; //Ko
            }
            if (htmlData.ToLower().Contains("Captcha".ToLower()))
            {
                return false; //Ko
            }
            if (htmlData.Contains("400 Bad Request"))
            {
                return false; //Ko
            }
            if (htmlData.Contains("ERROR: The requested URL could not be retrieved"))
            {
                return false; //Ko
            }
            if (htmlData.Contains("503 Service Unavailable"))
            {
                return false; //Ko
            }
            if (htmlData.Contains("ERR_ACCESS_DENIED"))
            {
                return false; //Ko
            }
            if (htmlData.Contains("Internal Server Error"))
            {
                return false; //Ko
            }
            if (htmlData.Contains("Sin embargo, no ha sido posible encontrar ninguna empresa"))
            {
                return false; //NoEsta
            }
            if (htmlData.Contains("Direct Justice Slp"))
            {
                return true; //Ok
            }
            if (htmlData.Contains("Informes recomendados sobre"))
            {
                return true; //Ok
            }

            return true; //NoEsta
        }

