        public WebRequest DevuelveWRequest(string url, int cuentaErrores, bool conProxy)
        {
            //objeto que envía la pregunta al Navegador
            WebRequest wRequest = null;

            //SIN PROXY
            if (conProxy == false)
            {
                wRequest = DevuelveWRequest(url);
            }
            else
            {//CON PROXI
                wRequest = DevuelveWRequest(url, cuentaErrores);
            }

            return wRequest;
        }

        public WebRequest DevuelveWRequest(string url)
        {
            // Creamos una variable WebRequest
            WebRequest wRequest = WebRequest.Create(url);

            return wRequest;
        }

        private WebRequest DevuelveWRequest(string url, int cuentaErrores)
        {
            // Creamos una variable WebRequest
            WebRequest wRequest = WebRequest.Create(url);
            WebProxy proxy = new WebProxy();
            ScProxyService proxyService = new ScProxyService();
            string proxyUrl = proxyService.GetProxy(cuentaErrores);
            if (proxyUrl.Length > 0)
            {
                //Console.WriteLine("\nPlease enter the Credentials (may not be needed)");
